package day06

fun main() {
    solution1(input).also(::println)
    solution2(input).also(::println)
}

private fun solution1(input: String) = solution(input, 4)
private fun solution2(input: String) = solution(input, 14)

private fun solution(input: String, length: Int) = input.asSequence()
    .windowed(length, 1)
    .mapIndexed { index, chars -> index to chars }
    .first { it.second.distinct().size == length }
    .first + length

private val input = """
    
""".trimIndent()